import React, { useState, useEffect } from 'react';

const CharityList = () => {
  const [categories, setCategories] = useState([]);
  const [locations, setLocations] = useState([]);
  const charities = [
    { name: 'Charity A', category: 'Education', location: 'New York' },
    { name: 'Charity B', category: 'Health', location: 'California' },
    { name: 'Charity C', category: 'Education', location: 'Texas' },
    { name: 'Charity D', category: 'Health', location: 'Florida' },
  ];

  useEffect(() => {
    const uniqueCategories = [...new Set(charities.map(charity => charity.category))];
    const uniqueLocations = [...new Set(charities.map(charity => charity.location))];
    
    setCategories(uniqueCategories);
    setLocations(uniqueLocations);
  }, []);

  return (
    <div>
      <h2>Categories</h2>
      <ul>
        {categories.map((category, index) => (
          <li key={index}>{category}</li>
        ))}
      </ul>

      <h2>Locations</h2>
      <ul>
        {locations.map((location, index) => (
          <li key={index}>{location}</li>
        ))}
      </ul>

      <h2>Charities</h2>
      <ul>
        {charities.map((charity, index) => (
          <li key={index}>
            {charity.name} - {charity.category} - {charity.location}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CharityList;
