import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const CharityList = () => {
  const [charities, setCharities] = useState([]);
  const [categories, setCategories] = useState([]);
  const [locations, setLocations] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filter, setFilter] = useState({ category: "", location: "" });

  useEffect(() => {
    // Fetch charities based on search term and filters
    const fetchCharities = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/api/charities",
          {
            params: { ...filter, search: searchTerm },
          }
        );
        setCharities(response.data.charities);
      } catch (error) {
        console.error("Error fetching charities", error);
      }
    };

    fetchCharities();
  }, [searchTerm, filter]);

  useEffect(() => {
    // Fetch categories for the category dropdown
    const fetchCategories = async () => {
      try {
        const response = await axios.get(
          ` http://localhost:5000/api/charities/categories`
        );
        setCategories(response.data.categories);
      } catch (error) {
        console.error("Error fetching categories", error);
      }
    };
  
    // Fetch locations for the location dropdown
    const fetchLocations = async () => {
      try {
        const response = await axios.get(
        `GET http://localhost:5000/api/charities/locations`
        );
        setLocations(response.data.locations);
      } catch (error) {
        console.error("Error fetching locations", error);
      }
    };
  
    fetchCategories();
    fetchLocations();
  }, []);
  

  return (
    <div>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <select
        value={filter.category}
        onChange={(e) => setFilter({ ...filter, category: e.target.value })}
      >
        <option value="">All Categories</option>
        {categories.map((category) => (
          <option key={category} value={category}>
            {category}
          </option>
        ))}
      </select>
      <select
        value={filter.location}
        onChange={(e) => setFilter({ ...filter, location: e.target.value })}
      >
        <option value="">All Locations</option>
        {locations.map((location) => (
          <option key={location} value={location}>
            {location}
          </option>
        ))}
      </select>

      <ul>
        {charities.map((charity) => (
          <li key={charity.id}>
            <Link to={`/charities/${charity.id}`}>
              <h3>{charity.name}</h3>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CharityList;
