import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CharityList = () => {
  const [charities, setCharities] = useState([]);
  const [categories, setCategories] = useState([]);
  const [locations, setLocations] = useState([]);

  // Fetch charities data
  useEffect(() => {
    const fetchCharities = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/charities');
        console.log('Charities API Response:', response.data);  // Log the response data
        const charitiesData = response.data;
        
        if (Array.isArray(charitiesData)) {  // Ensure the data is an array
          setCharities(charitiesData);
          
          // Extract unique categories and locations from charity list
          const uniqueCategories = [...new Set(charitiesData.map(charity => charity.category))];
          const uniqueLocations = [...new Set(charitiesData.map(charity => charity.location))];

          setCategories(uniqueCategories);
          setLocations(uniqueLocations);
        } else {
          console.error('Expected array but got:', charitiesData);
        }
      } catch (error) {
        console.error('Error fetching charities', error);
      }
    };
    
    fetchCharities();
  }, []);

  return (
    <div>
      <h2>Categories</h2>
      <ul>
        {categories.map((category, index) => (
          <li key={index}>{category}</li>
        ))}
      </ul>

      <h2>Locations</h2>
      <ul>
        {locations.map((location, index) => (
          <li key={index}>{location}</li>
        ))}
      </ul>

      <h2>Charities</h2>
      <ul>
        {Array.isArray(charities) && charities.map((charity, index) => (
          <li key={index}>
            {charity.name} - {charity.category} - {charity.location}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CharityList;
