import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // useNavigate instead of useNavigation
import '../styles/CharityRegistration.css';

const CharityRegistration = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [mission, setMission] = useState('');
  const [goals, setGoals] = useState('');
  const [projects, setProjects] = useState('');
  const navigate = useNavigate(); // Fixed navigate function
  
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      const token = localStorage.getItem('token'); // Retrieve token from localStorage
      if (!token) {
        throw new Error('No authentication token found. Please log in.');
      }
  
      // Make the API request to register the charity
      const response = await axios.post('http://localhost:5000/api/charities/register', {
        name,
        email,
        password,
        mission,
        goals,
        projects,
      }, {
        headers: {
          Authorization: `Bearer ${token}`, // Include the token in the request headers
        },
      });
      console.log(response.data.id);
      const { id } = response.data; // Adjust this based on the actual response structure
  
      alert('Charity registered successfully!');
      navigate(`/charity/${id}`); // Navigate to charity profile after registration
    } catch (error) {
      console.error('Error registering charity', error);
      alert('Error registering charity. Please try again.');
    }
  };
  
  return (
    <div className="charity-registration">
      <h1>Register Charity</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <textarea placeholder="Mission" value={mission} onChange={(e) => setMission(e.target.value)}></textarea>
        <textarea placeholder="Goals" value={goals} onChange={(e) => setGoals(e.target.value)}></textarea>
        <textarea placeholder="Projects" value={projects} onChange={(e) => setProjects(e.target.value)}></textarea>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default CharityRegistration;
