import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const CharityList = () => {
  const [charities, setCharities] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState({ category: '', location: '' });

  useEffect(() => {
    const fetchCharities = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/charities/char', {
          params: { ...filter, search: searchTerm }
        });
        setCharities(response.data.charities);
      } catch (error) {
        console.error('Error fetching charities', error);
      }
    };

    fetchCharities();
  }, [searchTerm, filter]);

  return (
    <div>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <select onChange={(e) => setFilter({ ...filter, category: e.target.value })}>
        <option value="">All Categories</option>
        {/* Add options dynamically based on available categories */}
      </select>
      <select onChange={(e) => setFilter({ ...filter, location: e.target.value })}>
        <option value="">All Locations</option>
        {/* Add options dynamically based on available locations */}
      </select>

      <ul>
        {charities.map((charity) => (
          <li key={charity.id}>
            {/* Link to the charity detail page */}
            <Link to={`/charities/${charity.id}`}>
              <h3>{charity.name}</h3>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CharityList;
