import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../styles/CharityProfile.css';

const CharityProfile = () => {
  const { id } = useParams(); 
  const [charity, setCharity] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCharity = async () => {
      try {
        const token = localStorage.getItem('token'); 
        if (!token) {
          throw new Error('No token found. Please log in.');
        }

        const response = await axios.get(`http://localhost:5000/api/charities/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`, // Include the token in the Authorization header
          },
        });
        setCharity(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching charity profile', error);
      }
    };

    fetchCharity();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="charity-profile">
      <h1>{charity.name}</h1>
      <p>Mission: {charity.mission}</p>
      <p>Goals: {charity.goals}</p>
      <p>Projects: {charity.projects}</p>
      <button>Edit Profile</button>
    </div>
  );
};

export default CharityProfile;
