import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import "../styles/Home.css";

const Home = () => {
  // Check if user is logged in by checking for a token in localStorage
  const isLoggedIn = !!localStorage.getItem('token'); // True if token exists, false otherwise

  // State for the notification form
  const [to, setTo] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  const [notificationStatus, setNotificationStatus] = useState('');

  // Handle sending the notification
  const handleSendNotification = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/send-notification', {
        to,
        subject,
        message
      });

      if (response.status === 200) {
        setNotificationStatus('Notification sent successfully');
      } else {
        setNotificationStatus('Failed to send notification');
      }
    } catch (error) {
      setNotificationStatus('Failed to send notification');
    }
  };

  return (
    <div className="home-container">
      {/* Top-right corner links */}
      <div className="top-right-links">
        {isLoggedIn ? (
          <>
            <Link to="/profile" className="home-link">Profile</Link>
            <Link to="/donations" className="home-link">Donations</Link>
          </>
        ) : (
          <>
            <Link to="/signup" className="home-link">Sign Up</Link>
            <Link to="/login" className="home-link">Login</Link>
          </>
        )}
      </div>

      <h1>Welcome to Charity Donation Platform</h1>
      <p>Your one-stop solution for making and managing donations.</p>

      <div className="home-links">
        {isLoggedIn ? (
          <>
            <Link to="/profile" className="home-link">Profile</Link>
            <Link to="/donations" className="home-link">Donations</Link>
            <Link to="/register-charity" className="home-link">Charities Registration</Link>
            <Link to="/charities" className="home-link">Charities</Link>
          </>
        ) : (
          <>
            <Link to="/signup" className="home-link">Sign Up</Link>
            <Link to="/login" className="home-link">Login</Link>
          </>
        )}
      </div>

      {/* Notification Form */}
      <div className="notification-form-container">
        <h2>Send Notification</h2>
        <form onSubmit={handleSendNotification}>
          <div>
            <label>To:</label>
            <input
              type="email"
              value={to}
              onChange={(e) => setTo(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Subject:</label>
            <input
              type="text"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Message:</label>
            <textarea
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
            />
          </div>
          <button type="submit">Send Notification</button>
        </form>

        {/* Notification status message */}
        {notificationStatus && <p>{notificationStatus}</p>}
      </div>
    </div>
  );
};

export default Home;
