import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../styles/ImpactReports.css'; // Add styles for the component

const ImpactReports = () => {
  const { charityId } = useParams();
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/charities/${charityId}/reports`);
        setReports(response.data.reports);
      } catch (error) {
        console.error('Error fetching impact reports:', error);
        setError('Error fetching impact reports');
      } finally {
        setLoading(false);
      }
    };

    fetchReports();
  }, [charityId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="impact-reports">
      <h1>Impact Reports</h1>
      {reports.length > 0 ? (
        <ul>
          {reports.map((report) => (
            <li key={report.id}>
              <h3>{report.title}</h3>
              <p>{report.description}</p>
              <p>Date: {new Date(report.date).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No impact reports available.</p>
      )}
    </div>
  );
};

export default ImpactReports;
