import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Import Link for navigation

const CharityList = () => {
  const [charities, setCharities] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState({ category: '', location: '' });
  const [categories, setCategories] = useState([]);
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    const fetchCharities = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/charities/char', {
          params: { ...filter, search: searchTerm },
        });
        setCharities(response.data.charities);
      } catch (error) {
        console.error('Error fetching charities', error);
      }
    };

    fetchCharities();
  }, [searchTerm, filter]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/charities/categories');
        setCategories(response.data.categories);
      } catch (error) {
        console.error('Error fetching categories', error);
      }
    };

    const fetchLocations = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/charities/locations');
        setLocations(response.data.locations);
      } catch (error) {
        console.error('Error fetching locations', error);
      }
    };

    fetchCategories();
    fetchLocations();
  }, []);

  return (
    <div>
      <h1>Browse Charities</h1>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <select
        value={filter.category}
        onChange={(e) => setFilter({ ...filter, category: e.target.value })}
      >
        <option value="">All Categories</option>
        {categories.map((category) => (
          <option key={category} value={category}>
            {category}
          </option>
        ))}
      </select>
      <select
        value={filter.location}
        onChange={(e) => setFilter({ ...filter, location: e.target.value })}
      >
        <option value="">All Locations</option>
        {locations.map((location) => (
          <option key={location} value={location}>
            {location}
          </option>
        ))}
      </select>

      <ul>
        {charities.map((charity) => (
          <li key={charity.id}>
            <h3>{charity.name}</h3>
            <p>{charity.mission}</p>
            <Link to={`/charity/${charity.id}`}>View Details</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CharityList;
