import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/AddDonation.css'; // Ensure this file exists

const AddDonation = () => {
  const [amount, setAmount] = useState('');
  const [charityId, setCharityId] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/donations/create-order', {
        amount,
        currency: 'INR', // You can set this dynamically if needed
      }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const { id, currency, amount } = response.data;

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID, // Use environment variable
        amount: amount,
        currency: currency,
        name: 'Charity Donation',
        description: 'Donate to charity',
        order_id: id,
        handler: async (response) => {
          // Handle payment success
          try {
            await axios.post('http://localhost:5000/api/donations', {
              amount,
              charityId,
              paymentId: response.razorpay_payment_id,
              orderId: response.razorpay_order_id,
              signature: response.razorpay_signature,
            }, {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            });

            alert('Donation successful!');
            navigate('/donations');
          } catch (error) {
            console.error('Error completing donation:', error);
            alert('Error completing donation. Please try again.');
          }
        },
        prefill: {
          name: '', // You can fill in user details if needed
          email: '',
        },
        theme: {
          color: '#3399cc',
        },
      };

      const paymentObject = new window.Razorpay(options);
      paymentObject.open();
    } catch (error) {
      console.error('Error creating order:', error);
      alert('Error creating donation order. Please try again.');
    }
  };

  return (
    <div className="add-donation">
      <h1>Add Donation</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Charity ID"
          value={charityId}
          onChange={(e) => setCharityId(e.target.value)}
          required
        />
        <button type="submit">Add Donation</button>
      </form>
    </div>
  );
};

export default AddDonation;
